mapper.update:
update askReplyBoard set refOrder = refOrder+1
where
ref = #{ref} and refOrder > #{refOrder}

update askReplyBoard set contents = #{contents}
where
askRno = #{askRno}

mapper.select:
select readTime from
askRead where askBno=#{bno} and readIp=#{clientIp}
order by readTime
desc limit 1;

mapper.update:
update askBoard set answerStatus = 'Y'
where askBno =
#{askBno}

update askBoard set isDelete = 'Y'
where askBno =
#{no}
